function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),c=document.querySelector("body"),t=document.querySelector(".nav__btns");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"),t.classList.remove("active")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"),t.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"),t.classList.remove("active"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav),console.log("Mark is 100/100");